import React, { useState, useEffect } from 'react';
import iconheart1 from '../../images/icons/icon-heart-01.png';
import iconheart2 from '../../images/icons/icon-heart-02.png';
import product1 from '../../images/product-01.jpg';

const Product = () => {
  const [activeFilter, setActiveFilter] = useState('All Product');
  const [products, setProducts] = useState([]);

  const categories = [
    { name: "All Product", api: "https://fakestoreapi.com/products/category" },
    { name: "Electronics", api: "https://fakestoreapi.com/products/category/electronics" },
    { name: "Jewelery", api: "https://fakestoreapi.com/products/category/jewelery" },
    { name: "Men's clothing", api: "https://fakestoreapi.com/products/category/" + encodeURIComponent("men's clothing") },
    { name: "Women's clothing", api: "https://fakestoreapi.com/products/category/" + encodeURIComponent("women's clothing") },
  ];

  const handleFilterClick = (filter) => {
    setActiveFilter(filter);
  };

  useEffect(() => {
    const fetchProducts = async () => {
      setIsLoading(true);
      try {
        const categoryPromises = categories.map(async (category) => {
          const response = await fetch(category.api);
          const data = await response.json();
          return {
            category: category.name,
            products: data.slice(0, 4),
          };
        });

        const results = await Promise.all(categoryPromises);
        setProducts(results);
      } catch (error) {
        console.error('Error fetching products:', error);
      } finally {
      }
    };

    fetchProducts();
  }, [activeFilter]);

  return (
    <div>
       <div class="bg0 m-t-23 p-b-140" style={{marginTop: '70px'}}>
		<div class="container">
			<div class="flex-w flex-sb-m p-b-52">
				<div class="flex-w flex-l-m filter-tope-group m-tb-10">
					<ul className="filter__controls"  style={{display: 'flex', gap: '20px', cursor: 'pointer'}}>
						{categories.map((category) => (
							<li
								key={category.name}
								className={activeFilter === category.name ? 'active' : ''}
								onClick={() => handleFilterClick(category.name)}>
								{category.name}
							</li>
						))}
					</ul>

					
				</div>

				<div class="flex-w flex-c-m m-tb-10">
					<div class="flex-c-m stext-106 cl6 size-104 bor4 pointer hov-btn3 trans-04 m-r-8 m-tb-4 js-show-filter">
						<i class="icon-filter cl2 m-r-6 fs-15 trans-04 zmdi zmdi-filter-list"></i>
						<i class="icon-close-filter cl2 m-r-6 fs-15 trans-04 zmdi zmdi-close dis-none"></i>
						 Filter
					</div>

					<div class="flex-c-m stext-106 cl6 size-105 bor4 pointer hov-btn3 trans-04 m-tb-4 js-show-search">
						<i class="icon-search cl2 m-r-6 fs-15 trans-04 zmdi zmdi-search"></i>
						<i class="icon-close-search cl2 m-r-6 fs-15 trans-04 zmdi zmdi-close dis-none"></i>
						Search
					</div>
				</div>
				
				{/* {/* Search product */} 
				<div class="dis-none panel-search w-full p-t-10 p-b-15">
					<div class="bor8 dis-flex p-l-15">
						<button class="size-113 flex-c-m fs-16 cl2 hov-cl1 trans-04">
							<i class="zmdi zmdi-search"></i>
						</button>

						<input class="mtext-107 cl2 size-114 plh2 p-r-15" type="text" name="search-product" placeholder="Search"/>
					</div>	
				</div>

				{/* Filter */}
				<div class="dis-none panel-filter w-full p-t-10">
					<div class="wrap-filter flex-w bg6 w-full p-lr-40 p-t-27 p-lr-15-sm">
						<div class="filter-col1 p-r-15 p-b-27">
							<div class="mtext-102 cl2 p-b-15">
								Sort By
							</div>

							<ul>
								<li class="p-b-6">
									<a href="#" class="filter-link stext-106 trans-04">
										Default
									</a>
								</li>

								<li class="p-b-6">
									<a href="#" class="filter-link stext-106 trans-04">
										Popularity
									</a>
								</li>

								<li class="p-b-6">
									<a href="#" class="filter-link stext-106 trans-04">
										Average rating
									</a>
								</li>

